import { Placeholder } from '@accelint/icons';
import { type FC, useState } from 'react';
import { Button } from '../button';
import { Icon } from '../icon';
import { Chip } from './';
import { DeletableChip } from './deletable';
import { ChipList } from './list';
import { SelectableChip } from './selectable';
import type { Meta, StoryObj } from '@storybook/react-vite';
import type { ChipListProps } from './types';

const meta = {
  title: 'Components/Chip',
  component: Chip,
  args: {
    size: 'medium',
  },
  argTypes: {
    children: {
      control: 'text',
    },
    size: {
      control: 'select',
      options: ['medium', 'small'],
    },
  },
} satisfies Meta<typeof Chip>;

export default meta;

export const Default: StoryObj<typeof meta> = {
  args: {
    children: 'Chip',
    color: 'info',
  },
  argTypes: {
    color: {
      control: 'select',
      options: ['info', 'advisory', 'normal', 'serious', 'critical'],
    },
  },
  render: ({ children, ...args }) => (
    <Chip {...args}>
      <Icon>
        <Placeholder />
      </Icon>
      {children}
    </Chip>
  ),
};

export const List: StoryObj<typeof meta> = {
  argTypes: {
    color: {
      control: 'select',
      options: ['info', 'advisory', 'normal', 'serious', 'critical'],
    },
  },
  parameters: {
    controls: {
      exclude: ['children', 'className', 'color'],
    },
  },
  render: ({ size }) => (
    <ChipList size={size}>
      {List.argTypes?.color?.options?.map((label) => (
        <Chip key={label} className='capitalize' color={label}>
          {label}
        </Chip>
      ))}
    </ChipList>
  ),
};

const selectableData = [
  {
    id: 'chip-1',
    label: 'Selectable chip',
  },
  {
    id: 'chip-2',
    label: 'Selectable chip',
  },
  {
    id: 'chip-3',
    label: 'Selectable chip',
  },
];

export const SelectableChipList: StoryObj<
  FC<ChipListProps<unknown> & { isDisabled?: boolean }>
> = {
  args: {
    disallowEmptySelection: false,
    selectionMode: 'multiple',
    isDisabled: false,
  },
  argTypes: {
    selectionMode: {
      control: 'select',
      options: ['none', 'single', 'multiple'],
    },
  },
  parameters: {
    controls: {
      exclude: ['children'],
    },
  },
  render: ({ children, id, className, style, size, isDisabled, ...rest }) => (
    <ChipList
      {...rest}
      disabledKeys={isDisabled ? selectableData.map(({ id }) => id) : undefined}
      items={selectableData}
      size={size}
    >
      {({ id, label }) => <SelectableChip id={id}>{label}</SelectableChip>}
    </ChipList>
  ),
};

const coloredSelectableData = [
  {
    id: 'up',
    label: '5 UP',
    color: 'normal' as const,
  },
  {
    id: 'down',
    label: '2 DOWN',
    color: 'critical' as const,
  },
  {
    id: 'all',
    label: '7 Total',
    color: 'info' as const,
  },
];

export const SelectableChipListWithColors: StoryObj<
  FC<ChipListProps<unknown> & { isDisabled?: boolean }>
> = {
  args: {
    disallowEmptySelection: false,
    selectionMode: 'single',
    isDisabled: false,
  },
  argTypes: {
    selectionMode: {
      control: 'select',
      options: ['none', 'single', 'multiple'],
    },
  },
  parameters: {
    controls: {
      exclude: ['children'],
    },
  },
  render: ({ children, id, className, style, size, isDisabled, ...rest }) => (
    <ChipList
      {...rest}
      disabledKeys={
        isDisabled ? coloredSelectableData.map(({ id }) => id) : undefined
      }
      items={coloredSelectableData}
      size={size}
    >
      {({ id, label, color }) => (
        <SelectableChip id={id} color={color}>
          {label}
        </SelectableChip>
      )}
    </ChipList>
  ),
};

const deletableChips = new Set([
  'Deletable chip 1',
  'Deletable chip 2',
  'Deletable chip 3',
]);

export const DeletableChipList: StoryObj<
  FC<ChipListProps<unknown> & { isDisabled?: boolean }>
> = {
  args: {
    isDisabled: false,
  },
  parameters: {
    controls: {
      exclude: ['children'],
    },
  },
  render: ({ children, id, style, size, isDisabled, ...rest }) => {
    const [chips, setChips] = useState(deletableChips);

    return (
      <>
        <ChipList
          {...rest}
          disabledKeys={isDisabled ? chips : undefined}
          items={Array.from(chips).map((label) => ({ id: label, label }))}
          size={size}
          onRemove={(keys) => setChips((prev) => prev.difference(keys))}
        >
          {({ id, label }) => <DeletableChip id={id}>{label}</DeletableChip>}
        </ChipList>
        <Button
          size='xsmall'
          variant='flat'
          onPress={() => setChips(deletableChips)}
        >
          Reset
        </Button>
      </>
    );
  },
};

