import { Placeholder } from '@accelint/icons';
import { Icon } from '../icon';
import { Link } from './';
import { LinkProvider } from './context';
import type { Meta, StoryObj } from '@storybook/react-vite';
import type { ReactNode } from 'react';

const meta = {
  title: 'Components/Link',
  component: Link,
  args: {
    allowsVisited: false,
    children: 'Text',
    isDisabled: false,
    isVisited: false,
  },
  argTypes: {
    allowsVisited: {
      control: 'boolean',
    },
    isDisabled: {
      control: 'boolean',
    },
  },
} satisfies Meta<typeof Link>;

export default meta;
type Story = StoryObj<typeof meta>;

export const Default: Story = {
  render: Link,
};

export const CustomVisitedStyles: Story = {
  args: {
    allowsVisited: true,
  },
  render: ({ children, ...args }) => (
    <LinkProvider
      {...args}
      className='enabled:visited:fg-accent-primary-pressed enabled:visited:underline'
    >
      <Link isVisited>{children}</Link>
    </LinkProvider>
  ),
};

export const Multiple: Story = {
  render: ({ children, ...args }) => (
    <>
      <p className='fg-primary-bold'>
        Some example text with a
        <Link {...args}>
          <Icon size='small'>
            <Placeholder />
          </Icon>
          {children as ReactNode}
        </Link>
        injected into it. There can be multiple
        <Link {...args}>
          <Icon size='small'>
            <Placeholder />
          </Icon>
          {children as ReactNode}
        </Link>
        here.
      </p>
    </>
  ),
};

