  return (
    <Icon size={size === 'small' ? 'medium' : 'large'}>
      {color === 'info' && <Information />}
      {color === 'advisory' && <Information />}
      {color === 'normal' && <Success />}
      {color === 'serious' && <Warning />}
      {color === 'critical' && <Problem />}
    </Icon>
  );
}

