'use client';

import type { Key } from '@react-types/shared';
import 'client-only';
import { useCallback, useMemo } from 'react';
import {
  isOptionGroupArray,
  useValueSelector,
  type ValueSelectorProps,
} from 'react-querybuilder';
import { ComboBoxField } from '../combobox-field';
import { OptionsItem } from '../options/item';
import { OptionsSection } from '../options/section';

export function ValueSelector(props: ValueSelectorProps) {
  const {
    handleOnChange,
    disabled,
    listsAsArrays,
    options: optionsProp,
    multiple,
    title,
    validation,
    value,
    ...rest
  } = props;

  const { onChange, val } = useValueSelector({
    handleOnChange,
    listsAsArrays,
    multiple,
    value,
  });

  const handleSelectionChange = useCallback(
    (selection: Key | null) => {
      if (selection) {
        onChange(`${selection}`);
      }
    },
    [onChange],
  );

  const options = useMemo(
    () =>
      isOptionGroupArray(optionsProp)
        ? optionsProp.map((section) => (
            <OptionsSection key={section.label} header={section.label}>
              {section.options.map((option) => (
                <OptionsItem id={option.name} key={option.name}>
                  {option.label}
                </OptionsItem>
              ))}
            </OptionsSection>
          ))
        : optionsProp.map((option) => (
            <OptionsItem
              textValue={option.label}
              id={option.name}
              key={option.name}
            >
              {option.label}
            </OptionsItem>
          )),
    [optionsProp],
  );

  return (
    <ComboBoxField
      size='small'
      isDisabled={disabled}
      {...rest}
      selectedKey={Array.isArray(val) ? val[0] : val}
      aria-labelledby={title}
      onSelectionChange={handleSelectionChange}
    >
      {options}
    </ComboBoxField>
  );
}
