'use client';

import 'client-only';
import { createContext } from 'react';
import type { ContextValue } from 'react-aria-components';
import type { ProviderProps } from '@/lib/types';
import type { SearchFieldProps } from './types';

/**
 * Context for SearchField component
 *
 * Provides context for SearchField component to share props
 */
export const SearchFieldContext =
  createContext<ContextValue<SearchFieldProps, HTMLDivElement>>(null);

/**
 * Provider for SearchField component
 *
 * Allows setting default props for all SearchField components within
 */
export function SearchFieldProvider({
  children,
  ...props
}: ProviderProps<SearchFieldProps>) {
  return (
    <SearchFieldContext.Provider value={props}>
      {children}
    </SearchFieldContext.Provider>
  );
}

