  return (
    <tr
      {...rest}
      ref={ref}
      className={clsx('group/row', styles.row, className)}
      data-pinned={row?.getIsPinned() || null}
      data-selected={row?.getIsSelected() || null}
    >
      {children ||
        cells?.map((cell) => <TableCell key={cell.id} cell={cell} />)}
    </tr>
  );
}

