        key: 'blue-jay',
        parentKey: 'north-american-birds',
        label: 'Blue jay',
        isVisible: true,
        values: {
          description: 'cyanocitta cristata',
          iconPrefix: <Placeholder />,
          hasWarning: true,
        },
      },
      {
        key: 'gray-catbird',
        parentKey: 'north-american-birds',
        label: 'Gray catbird',
        isVisible: true,
        values: {
          description: 'dumetella carolinensis',
          iconPrefix: <Placeholder />,
        },
      },
      {
        key: 'black-capped-chickadee',
        parentKey: 'north-american-birds',
        label: 'Black-capped chickadee',
        isDisabled: true,
        isVisible: false,
        values: {
